package report01;

public class GoodsInfo {

	String code;
	String name;
	String color;
	int price;
	String goodsInfo;
	String maker;
	int stock;
	
	public GoodsInfo(String code,String name, String color, int price, String goodsInfo,String maker, int stock) {
		// TODO Auto-generated constructor stub
		this.code = code;
		this.name = name;
		this.color = color;
		this.price = price;
		this.goodsInfo = goodsInfo;
		this.maker = maker;
		this.stock = stock;
	}

}
